import React from "react";
import "./dashboard.styles.css";

export const Dashboard = () => {
  const date = new Date();
  const year = date.getFullYear();
  const month = date.toLocaleString("default", { month: "long" });
  const day = date.getDay();
  const hours = date.getHours();
  const mins = date.getMinutes();

  const fullDate = `${month} ${day}, ${year}, ${hours}:${mins}`;

  return (
    <div className="dash-container">
      <header>Your Dashboard</header>
      <span>{fullDate}</span>

      <div className="totals-container">
        <div className="total-invoices">
          <span>Total Invoices Unpaid:</span>
          <div className="container-btns"></div>
          <button className="btn">New Invoice</button>
          <button className="btn">View All Invoices</button>
        </div>

        <div className="total-bills">
          <span>Total Bills Unpaid:</span>
          <button className="btn">New Bill</button>
          <button className="btn">View All Bills</button>
        </div>
      </div>
    </div>
  );
};
