import React from "react";
import { AiOutlineCloseCircle } from "react-icons/ai";
import "./windowcard.styles.css";

export const WindowCard = ({ opts }) => {
  const { width, height, qty, notes, id, deleteWindow } = opts;

  return (
    <div id={id} className="windowcard-container">
      <AiOutlineCloseCircle onClick={deleteWindow} className="close-icon" />
      {/* <h3 className="window-id">{id}.</h3> */}
      <h3 className="hdr window-width">
        {width}W x {height}H
      </h3>
      {/* <h3 className="hdr window-height">Height: /h3> */}
      <h3 className="hdr window-qty">Qty: {qty}</h3>
      <h3 className="hdr window-price">
        Price: ${Math.round(width * height * 0.0695) * qty}
      </h3>
      <button className="btn-window-notes">NOTES</button>
    </div>
  );
};
