import React, { useState } from "react";
import { FaQuestionCircle } from "react-icons/fa";
import { IoMdArrowDropupCircle } from "react-icons/io";
import "./faq.styles.css";

export const Faq = () => {
  const [clicked, setClicked] = useState({
    first: false,
    second: false,
    third: false,
    fourth: false,
  });

  console.log(clicked);

  const swapIcons = (event) => {
    const targ = event.target.closest(".faq-question");
    const checker = !targ.classList.contains("eban");
    !targ.classList.contains("eban")
      ? targ.classList.add("eban")
      : targ.classList.remove("eban");
    const id = targ.id + "";
    setClicked({ ...clicked, [id]: checker ? true : false });
    console.log(checker);
  };

  const iconDecider = (event) => {
    const targ = event.target.closest(".faq-question");
    if (targ.classList.contains("eban"))
      return <FaQuestionCircle className="question-icon" onClick={swapIcons} />;
    if (!targ.classList.contains("eban"))
      return (
        <IoMdArrowDropupCircle className="question-icon" onClick={swapIcons} />
      );
  };

  return (
    <div className="faq-container">
      <h1 className="faq-header">Frequently Asked Questions</h1>

      <div className="questions-container">
        <span id="first" className="faq-question">
          How Much does it cost to tint my vehicle?
        </span>
        <span id="second" className="faq-question">
          How Much does it cost to instal PPF on my vehicle?
        </span>
        <span id="third" className="faq-question">
          <FaQuestionCircle onClick={swapIcons} className="question-icon" /> Is
          my vehicle bullet proof now?
        </span>
        <span id="fourth" className="faq-question">
          <FaQuestionCircle onClick={swapIcons} className="question-icon" />{" "}
          What is the difference between dyed film and ceramic film?
        </span>
      </div>
    </div>
  );
};
