import React, { useState } from "react";
import { FaQuestionCircle } from "react-icons/fa";
import { IoMdArrowDropupCircle } from "react-icons/io";
import "./faq.styles.css";

export const Faq = () => {
  const swapIcons = (event) => {
    const targ = event.target.closest(".faq-question");
    if (!targ.classList.contains("eban")) {
      targ.classList.add("eban");
      return (
        <IoMdArrowDropupCircle onClick={swapIcons} className="question-icon" />
      );
    }

    if (targ.classList.contains("eban")) {
      targ.classList.remove("eban");
      return <FaQuestionCircle onClick={swapIcons} className="question-icon" />;
    }
  };

  return (
    <div className="faq-container">
      <h1 className="faq-header">Frequently Asked Questions</h1>

      <div className="questions-container">
        <span className="faq-question">
          <FaQuestionCircle onClick={swapIcons} className="question-icon" /> How
          Much does it cost to tint my vehicle?
        </span>
        <span className="faq-question">
          <FaQuestionCircle className="question-icon" /> How Much does it cost
          to instal PPF on my vehicle?
        </span>
        <span className="faq-question">
          <FaQuestionCircle className="question-icon" /> Is my vehicle bullet
          proof now?
        </span>
        <span className="faq-question">
          <FaQuestionCircle className="question-icon" /> What is the difference
          between dyed film and ceramic film?
        </span>
      </div>
    </div>
  );
};
