import React, { useState, useEffect } from "react";
import { getTotal } from "../state/store";
import { HiDocumentAdd } from "react-icons/hi";
import { InvoiceDetails } from "./InvoiceDetails";
import "./invoice.styles.css";

const totalCost = getTotal((state) => state.total);

export const InvoiceNew = () => {
  const [items, setItems] = useState([]);
  const [total, setTotal] = useState(totalCost);
  const [dealer, setDealer] = useState(false);

  const addItem = () => {
    setItems([...items, 1]);
  };

  // const grandTotal = () => {
  //   const gavno = document.getElementsByClassName("total-price");

  //   const newTotal = Array.from(gavno).map((el) => +el.innerText);

  //   const total = newTotal.reduce(
  //     (prevVal, currentVal) => prevVal + currentVal,
  //     0
  //   );
  //   setTotal(total);
  // };

  // useEffect(() => {
  //   const gavno = document.getElementsByClassName("total-price");

  //   const newTotal = Array.from(gavno).map((el) => +el.innerText);

  //   const total = newTotal.reduce(
  //     (prevVal, currentVal) => prevVal + currentVal,
  //     0
  //   );
  //   setTotal(total);
  // }, [items]);

  return (
    <div className="invoice-container">
      <div className="invoice-top">
        <h2 className="invoice-hdr">INVOICE</h2>
        <h2 className="invoice-bus">
          Living Skies Window Tint 160C 16th Street West, Prince Albert, SK, S6V
          3V3
        </h2>
      </div>

      <div className="date-number">
        <span className="invoice-number">Invoice #</span>
        <input type="date" id="date-invoice" />
      </div>
      <div className="billto">
        <h2 className="bill">BILL TO</h2>
        <h2 className="bill">SHIP TO</h2>
      </div>

      <div className="addys">
        <div className="billing">
          <input
            className="invoice-input"
            type="text"
            id="customername"
            placeholder="Customer Name"
          />
          <input
            className="invoice-input"
            type="text"
            id="customeraddress"
            placeholder="Address"
          />
          <input
            className="invoice-input"
            type="text"
            id="customerphone"
            placeholder="Phone Number"
          />
          <input
            className="invoice-input"
            type="email"
            id="customeremail"
            placeholder="Email"
          />
        </div>

        <div className="shipping">
          <input
            className="invoice-input"
            type="text"
            id="customername"
            placeholder="Customer Name"
          />
          <input
            className="invoice-input"
            type="text"
            id="customeraddress"
            placeholder="Address"
          />
          <input
            className="invoice-input"
            type="text"
            id="customerphone"
            placeholder="Phone Number"
          />
          <input
            className="invoice-input"
            type="email"
            id="customeremail"
            placeholder="Email"
          />
        </div>
      </div>

      <HiDocumentAdd onClick={addItem} className="new-item" />
      <div id="details-div" className="all-details">
        {items.map((item, id) => (
          <InvoiceDetails key={id} />
        ))}
      </div>

      <div className="taxes-total">
        <span>PST:</span>
        <span>GST/HST:</span>
        <span>GRAND TOTAL:{total}</span>
      </div>
    </div>
  );
};
