import React from "react";
import create from "zustand";
import { Outlet } from "react-router-dom";
import { FaFileInvoiceDollar, FaList } from "react-icons/fa";
import { SiSimpleanalytics } from "react-icons/si";
import { MdSettings, MdLogout } from "react-icons/md";
import "./navbar.styles.css";

const useBearStore = create((set) => ({
  bears: 7,
  increasePopulation: () => set((state) => ({ bears: state.bears + 1 })),
  removeAllBears: () => set({ bears: 0 }),
}));

const useButton = create((set) => ({
  buttonClick: "",
  setButton: (event) => set((state) => ({ buttonClick: event.target.id })),
}));

const newId = (event) => console.log(event.target.id);

export const NavBar = () => {
  const bears = useBearStore((state) => state.bears);
  const addBear = useBearStore((state) => state.increasePopulation);

  const setBut = useButton((state) => state.setButton);
  const baton = useButton((state) => state.buttonClick);

  return (
    <div className="accounting-navbar-container">
      <div className="acc-inner-container">
        <h2 className="user-container">HELLO USER</h2>

        <div className="section-container">
          <header className="header-txt">SALES</header>
          <section className="section-sales">
            <span id="newinvoice" className="dash-link">
              <FaFileInvoiceDollar className="icon-navbar" /> New Invoice
            </span>
            <span id="viewinvoices" className="dash-link">
              <FaList className="icon-navbar" /> View All Invoices
            </span>
            <span id="reportsinvoices" className="dash-link">
              <SiSimpleanalytics className="icon-navbar" /> Reports
            </span>
          </section>
        </div>

        <div className="section-container">
          <header className="header-txt">PURCHASES</header>
          <section className="section-bills">
            <span id="newbill" className="dash-link">
              <FaFileInvoiceDollar className="icon-navbar" /> New Bill
            </span>
            <span id="viewbills" className="dash-link">
              <FaList className="icon-navbar" /> View All Bills
            </span>
            <span id="reportsbills" className="dash-link">
              <SiSimpleanalytics className="icon-navbar" /> Reports
            </span>
          </section>
        </div>

        <div className="footer-container">
          <span className="settings">
            {" "}
            <MdSettings className="icon-navbar" /> Settings
          </span>
          <span className="logout">
            {" "}
            <MdLogout className="icon-navbar" /> Log Out
          </span>
          <span>Baton Counter: {baton}</span>
          <button onClick={setBut}>Add Bears</button>
        </div>
      </div>
      <Outlet />
    </div>
  );
};
