import React, { useState } from "react";
import { getTotal } from "../state/store";

export const InvoiceDetails = () => {
  const [qty, setQty] = useState(0);
  const [price, setPrice] = useState(0);

  const setTot = getTotal((state) => state.setTotal);

  const changeQty = (event) => {
    const val = Math.floor(+event.target.value).toFixed(2);
    setQty(val);
    grandTotal();
  };

  const changePrice = (event) => {
    const val = Math.floor(+event.target.value).toFixed(2);
    setPrice(val);
    grandTotal();
  };

  const grandTotal = () => {
    const gavno = document.getElementsByClassName("total-price");

    const newTotal = Array.from(gavno).map((el) => +el.innerText);

    const total = newTotal.reduce(
      (prevVal, currentVal) => prevVal + currentVal,
      0
    );
    setTot(total);
  };

  return (
    <div className="invoice-details">
      <div className="quantity">
        <h2 className="details-headers">QTY</h2>
        <input
          onChange={changeQty}
          className="details-input input-qty"
          type="number"
          placeholder="How Many"
        />
      </div>
      <div className="description">
        <h2 className="details-headers">DESCRIPTION</h2>
        <input
          className="details-input"
          type="text"
          placeholder="Description"
        />
      </div>
      <div className="unit-cost">
        <h2 className="details-headers">UNIT COST</h2>
        <input
          onChange={changePrice}
          className="details-input"
          type="text"
          placeholder="Price per"
        />
      </div>
      <div className="total">
        <h2 className="details-headers">TOTAL</h2>
        <span className="total-price">{(qty * price).toFixed(2)}</span>
        {/* <button onClick={calcTotal}>totals</button> */}
      </div>
    </div>
  );
};
