import React, { useState } from "react";

export const InvoiceDetails = () => {
  const [qty, setQty] = useState(0);
  const [price, setPrice] = useState(0);

  console.log(qty, price);

  const changeQty = (event) => {
    setQty(+event.target.value);
  };

  const changePrice = (event) => {
    setPrice(+event.target.value);
  };

  return (
    <div className="invoice-details">
      <div className="quantity">
        <h2 className="details-headers">QTY</h2>
        <input
          onChange={changeQty}
          className="details-input input-qty"
          type="number"
          placeholder="How Many?"
        />
      </div>
      <div className="description">
        <h2 className="details-headers">DESCRIPTION</h2>
        <input
          className="details-input"
          type="text"
          placeholder="Description"
        />
      </div>
      <div className="unit-cost">
        <h2 className="details-headers">UNIT COST</h2>
        <input
          onChange={changePrice}
          className="details-input"
          type="text"
          placeholder="Description"
        />
      </div>
      <div className="total">
        <h2 className="details-headers">TOTAL</h2>
        <span className="total-price">${qty * price}</span>
        {/* <button onClick={calcTotal}>totals</button> */}
      </div>
    </div>
  );
};
